import javax.script.ScriptEngine;import javax.script.ScriptEngineManager;import javax.script.ScriptException;import javax.swing.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class GoNumListener implements ActionListener {    JTextField input;    JFrame window;    public GoNumListener(JTextField input, JFrame window){        this.input = input;        this.window = window;    }    @Override    public void actionPerformed(ActionEvent e) {        String name = ((JButton)e.getSource()).getText();        if(!(name == "C" || name == "="))            input.setText(input.getText() + name);        if(name == "=" )            result();        if(name == "C")            input.setText("");        window.repaint();    }    public void result(){        ScriptEngineManager factory = new ScriptEngineManager();        ScriptEngine calc = factory.getEngineByName("JavaScript");        try{            input.setText("" + calc.eval(input.getText()));        }        catch (ScriptException e){            input.setText("Ошибка!");            e.printStackTrace();        }    }}