import javax.script.ScriptEngine;import javax.script.ScriptEngineManager;import javax.script.ScriptException;import javax.swing.*;import java.awt.*;import java.awt.event.KeyEvent;public class KeyDispatcher implements KeyEventDispatcher{    JTextField input;    public KeyDispatcher(JTextField i){        input = i;    }    @Override    public boolean dispatchKeyEvent(KeyEvent e) {        if(e.getKeyCode() == KeyEvent.VK_ENTER){            result();        }        if(e.getKeyCode() == KeyEvent.VK_ESCAPE){            input.setText("");        }        return  false;    }    public void result(){        ScriptEngineManager factory = new ScriptEngineManager();        ScriptEngine calc = factory.getEngineByName("JavaScript");        try{            input.setText("" + calc.eval(input.getText()));        }        catch (ScriptException e){            e.printStackTrace();        }    }}